import React, { useState, useEffect, useRef } from "react";
import "../styles/Carousel.css";

interface CarouselProps {
  images: { src: string; alt?: string }[];
}

const AUTO_PLAY_INTERVAL = 4500; // Slower auto-play interval

const Carousel: React.FC<CarouselProps> = ({ images }) => {
  const [current, setCurrent] = useState(0);
  const [direction, setDirection] = useState<"left" | "right">("right");
  const [animation, setAnimation] = useState<"slide-in-left" | "slide-in-right" | "slide-out-left" | "slide-out-right" | "">("slide-in-right");
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  const goToSlide = (idx: number, dir: "left" | "right") => {
    setDirection(dir);
    setAnimation(dir === "right" ? "slide-out-left" : "slide-out-right");
    setTimeout(() => {
      setCurrent(idx);
      setAnimation(dir === "right" ? "slide-in-right" : "slide-in-left");
    }, 500);
  };

  const prevSlide = () => {
    goToSlide(current === 0 ? images.length - 1 : current - 1, "left");
  };

  const nextSlide = () => {
    goToSlide(current === images.length - 1 ? 0 : current + 1, "right");
  };

  useEffect(() => {
    timeoutRef.current = setTimeout(() => {
      nextSlide();
    }, AUTO_PLAY_INTERVAL);
    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [current, images.length]);

  if (images.length === 0) return null;

  return (
    <div className="carousel-centered-outer">
      <div className="carousel-centered-inner">
        <h2 className="carousel-title">
          Refined Painting. Elevated Living
        </h2>
        <div className="carousel">
          <button
            onClick={prevSlide}
            className="carousel-btn left"
            aria-label="Previous"
          >
            &#8592;
          </button>
          <div className="carousel-img-wrapper">
            <img
              src={images[current].src}
              alt={images[current].alt || `Work example ${current + 1}`}
              className={`carousel-img ${animation}`}
              onAnimationEnd={() => setAnimation("")}
            />
          </div>
          <button
            onClick={nextSlide}
            className="carousel-btn right"
            aria-label="Next"
          >
            &#8594;
          </button>
        </div>
        <div className="carousel-dots">
          {images.map((_, idx) => (
            <span
              key={idx}
              className={`carousel-dot${idx === current ? " active" : ""}`}
              onClick={() => goToSlide(idx, idx > current ? "right" : "left")}
            />
          ))}
        </div>
        <button className="carousel-cta-btn">Book a free quote</button>
      </div>
    </div>
  );
};

export default Carousel;
